export class UserDetailsComponent {
  constructor(userService, requestService, workerService, router, route) {
    this.userService = userService;
    this.requestService = requestService;
    this.workerService = workerService;
    this.router = router;
    this.route = route; //------------------------------------------------------generalne stvari------------------------------------------------------

    this.isLoading = true;
    this.hasRequest = false;
    this.dataChange = false;
    this.dataChangeFail = false;
    this.passChange = false;
    this.passChangeFail = false;
    this.badChosen = false;
    this.imgChange = false;
    this.imgChangeFail = false;
    this.newSuccess = false;
    this.editSuccess = false;
  }

  ngOnInit() {
    this.logged = JSON.parse(sessionStorage.getItem("loggedIn"));

    if (!this.logged || this.logged.type != 2) {
      sessionStorage.clear();
      this.router.navigate(['']);
    } else {
      let username = this.route.snapshot.paramMap.get("username");
      this.userService.fetchUsers().subscribe(users => {
        this.allUsers = users;
        this.userService.getUser(username).subscribe(usr => {
          this.user = usr;
          this.email = this.user.email;
          this.phone = this.user.phone;

          if (usr.type == 1) {
            this.agencyName = this.user.agencyName;
            this.address = this.user.address;
            this.desc = this.user.desc;
            this.userService.getReviews(username).subscribe(revs => {
              this.reviews = revs;
              this.workerService.getAllFromAgency(username).subscribe(allWorkers => {
                this.workers = allWorkers;
                this.requestService.getWorkersRequest(username).subscribe(req => {
                  if (req) {
                    this.hasRequest = true;
                    this.request = req;
                  } else {
                    this.hasRequest = false;
                    this.request = null;
                  }

                  this.isLoading = false;
                });
              });
            });
          } else {
            this.firstname = this.user.firstName;
            this.lastname = this.user.lastName;
            this.isLoading = false;
          }
        });
      });
    }
  }

  fileChosen(event) {
    var _this = this;

    return _asyncToGenerator(function* () {
      //isto kao kod registracije
      if (event.target.value) {
        _this.img = event.target.files[0];
        let img = new Image();
        img.src = URL.createObjectURL(_this.img);
        yield img.decode();
        if (img.naturalWidth < 100 || img.naturalWidth > 300 || img.naturalHeight < 100 || img.naturalHeight > 300) _this.badChosen = true;else _this.badChosen = false;
      } else {
        _this.badChosen = false;
        _this.img = null;
      }
    })();
  }

  changeImg() {
    //isto kao kod registracije
    let form = new FormData();
    form.append("profile_pic", this.img);
    form.append("user", this.logged.username);
    this.userService.editImg(form).subscribe(response => {
      if (response['status'] == 'ok') {
        this.imgChange = true;
        this.imgChangeText = "Ikonica uspešno izmenjena!";
        this.logged.img = response["newImg"];
        sessionStorage.setItem("loggedIn", JSON.stringify(this.logged));
        this.ngOnInit();
      } else {
        this.imgChangeFail = true;
        this.imgChangeText = "Greška pri izmeni ikonice!";
      }
    });
  }

  changeClientData() {
    for (let u of this.allUsers) {
      if (this.email == u.email) {
        alert("Email nije jedinstven!");
        return;
      }
    }

    this.userService.editClientData(this.logged.username, this.firstname, this.lastname, this.phone, this.email).subscribe(response => {
      if (response['status'] == "ok") {
        //podaci izmenjeni u bazi, sada moraju da se izmene u sessionStorage
        this.dataChange = true;
        this.logged.firstName = this.firstname;
        this.logged.lastName = this.lastname;
        this.logged.phone = this.phone;
        this.logged.email = this.email;
        sessionStorage.setItem("loggedIn", JSON.stringify(this.logged));
      } else this.dataChangeFail = true;
    });
  }

  changeAgencyData() {
    for (let u of this.allUsers) {
      if (this.email == u.email) {
        alert("Email nije jedinstven!");
        return;
      }
    }

    this.userService.editAgencyData(this.logged.username, this.logged.agencyName, this.address, this.desc, this.email, this.phone).subscribe(response => {
      if (response['status'] == "ok") {
        //podaci izmenjeni u bazi, sada moraju da se izmene u sessionStorage
        this.dataChange = true;
        this.logged.agencyName = this.agencyName;
        this.logged.address = this.address;
        this.logged.desc = this.desc;
        this.logged.phone = this.phone;
        this.logged.email = this.email;
        sessionStorage.setItem("loggedIn", JSON.stringify(this.logged));
      } else this.dataChangeFail = true;
    });
  }

  addWorker() {
    this.workerService.addWorker(this.logged.username, this.newFirstName, this.newLastName, this.newEmail, this.newPhone, this.newSpecialty).subscribe(response => {
      if (response['status'] == "ok") {
        this.newSuccess = true;
        this.ngOnInit();
      }
    });
  }

  fill(i) {
    this.editId = this.workers[i].workerId;
    this.editFirstName = this.workers[i].firstName;
    this.editLastName = this.workers[i].lastName;
    this.editEmail = this.workers[i].email;
    this.editPhone = this.workers[i].phone;
    this.editSpecialty = this.workers[i].specialty;
  }

  editWorker() {
    this.workerService.editWorker(this.editId, this.editFirstName, this.editLastName, this.editEmail, this.editPhone, this.editSpecialty).subscribe(response => {
      if (response['status'] == "ok") {
        this.editSuccess = true;
        this.ngOnInit();
      }
    });
  }

  removeWorker(id) {
    this.workerService.removeWorker(id).subscribe(response => {
      if (response['status'] == "ok") {
        this.ngOnInit();
      }
    });
  }

  acceptRequest() {}

  refuseRequest() {}

}

